package feb19;

public class ArithmeticEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 10;
		int j = 20;
		
		int k;
		
		k = i+j;
		
		System.out.println("Sum of i and j is : " + k);
		
		int mul;
		mul = i * j;
		System.out.println("Mul of i and j is : " + mul);
		
		int div;
		div = j / i;
		System.out.println("Div of j and i is :" + div);
		
		int mod;
		mod = 2022 % 4;
		System.out.println("mod of j of i is : " + mod);
		
	}

}
