package feb23;

public class ForLoopEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//this is programe to print 1 to 10
		for(int i=1; i<=10; i++) {
			System.out.println(i);
		}
		
		// pragram to print 10 to 1
		for(int i=10; i >=1; i--) {
			System.out.println(i);
		}
		
		
		
	}

}
